import { Component, TemplateRef } from "@angular/core";
import { ModalController } from "../../../providers/modal/modalController";
import { ModalOptions } from "../modal";

export interface AlertOptions extends ModalOptions {
    title?: string;
    image?: string;
    content?: string;
    contentTpl?: TemplateRef<any>;
}

@Component({
    templateUrl: "./alert.html",
})
export class AlertComponent {
    constructor(
        private modalController: ModalController
    ) { }
    image: string = "";
    content: string = "";
    contentTpl: TemplateRef<any>;

    ngOnInit() {
        let options = this.modalController.params;
        if (typeof options == "string") {
            options = {
                content: options,
            }
        }
        this.deal(options);
    }

    deal(options: AlertOptions) {
        this.content = options.content;
        this.image = options.image;
        this.contentTpl = options.contentTpl;
    }

    ngOnDestroy() { }
}