export class ModalController {
	constructor(public params: any) { }

	/**获取参数 */
	get(name: string) {
		if (this.params && this.params[name]) {
			return this.params[name]
		} else {
			return;
		}
	}

	/**响应消息 */
	sendMessage(data?: any) { }
	/**响应失败 */
	sendError(data?: any) { }

	/**关闭弹框 */
	close() { }
}