import { Component } from "@angular/core";
import { ModalProvider } from "../../../src/index";

@Component({
	templateUrl: "./tabs.html",
	selector: "test-tabs",
	styleUrls: ["./tabs.scss"],
})
export class TabsComponent {

	constructor(
		private model: ModalProvider,
	) { }
	logs1 = []
	logs2 = []

	enter(i, e) {
		if (e) {
			this['logs' + i].unshift("你好，这是第一次打开！ " + (new Date().toLocaleString()));
		} else {
			this['logs' + i].unshift("又见面了！ " + (new Date().toLocaleString()));
		}
	}
	leave(i) {
		this['logs' + i].unshift("再回！ " + (new Date().toLocaleString()));
	}
}